import styled from "styled-components";

export const Container = styled.div`
  margin-top: 15px;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  align-items: center;
  nav {
    ul {
      display: flex;
      align-items: center;
      justify-content: center;
      flex-wrap: wrap;
      li {
        margin: 0 20px;
      }
    }
  }

  @media (max-width: 400px) {
    background-color: black;
  }
`;
