import { createGlobalStyle } from "styled-components";

export default createGlobalStyle`

*{
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    :root {
        --mainBackground: #DCDCDC;
        --cartBackground: #F8F9FA;
    }

    body {
        background: var(--mainBackground);

    }

    a {
        text-decoration: none;
    }


`;
