import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 70vh;

  .inputText {
    display: flex;
    flex-direction: column;

    input {
      margin: 10px 0;
    }
  }
`;
