import { useContext } from "react";

function Header() {
  const { cart } = useContext(CartContext);

  return (
    <Container>
      <h2>Kenzie Shop</h2>

      <nav>
        <ul>
          <li>
            carrinho
            <Badge badgeContent={cart.length} color="secondary">
              <BsCart />
            </Badge>
          </li>
          <li>
            entrar <AiOutlineLogin />
          </li>
        </ul>
      </nav>
    </Container>
  );
}

export default Header;
